import React, { useState, useEffect, Component } from "react";
import { db } from "../firebase";

import "./LinksForm.css"



const LinksForm = (props) => {
  const initialStateValues = {

    name: "",
    formation: "",
    document: "",
    date_of_birth: "",
    date_of_admission: "",
    egress_date: "",
    trimester: "",

  };

  const [values, setValues] = useState(initialStateValues);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setValues({ ...values, [name]: value });
  };


  const handleSubmit = (e) => {
    e.preventDefault();


    props.addOrEditLink(values);
    setValues({ ...initialStateValues });
  };

  const getLinkById = async (id) => {
    const doc = await db.collection("links").doc(id).get();
    setValues({ ...doc.data() });
  };

  useEffect(() => {
    if (props.currentId === "") {
      setValues({ ...initialStateValues });
    } else {
      getLinkById(props.currentId);
    }

  }, [props.currentId]);

  data: [
    { name: "Cc", boxes: [1, 2, 3], selected: null },
    { name: "Ti", boxes: [1, 2, 3], selected: null },
    { name: "Ce", boxes: [1, 2, 3], selected: null },
    { name: "Nip", boxes: [1, 2, 3], selected: null }
  ]


class test extends Component {
  constructor(props) {
    super(props);
    this.state = {};
    const data = [
      { name: Cc ,checked: 2 },
      { name: Ti,checked: 3 },
      { name: Ce ,checked: 1 },
      { name: Nip ,checked: 4 }
    ];
  }


  render() {
  return (
    <div className="container-main">

      <form onSubmit={handleSubmit} className="card card-body border-primary">
        <h1 className="mb-4 text-center">Alumnos</h1>
        <div className="form-group input-group">
          <div className="input-group-text bg-light">
            <i className="material-icons">person</i>
          </div>
          <input
            type="text"
            value={values.name}
            name="name"
            placeholder="Ingresa tu nombre completo"
            className="form-control"
            onChange={handleInputChange}
          />
        </div>

        <div className="form-group input-group">
          <div className="input-group-text bg-light mb-4">
            <i className="material-icons">people</i>
          </div>
          <input
            type="text"
            className="form-control"
            placeholder="Formación"
            value={values.formation}
            name="formation"
            onChange={handleInputChange}
          />
        </div>


      <>
        {data.map(items => (
          <tr>
            <td>{items.name}</td>
            <td>
              <div>
                <input type="checkbox" value="1" checked={true} />
              </div>
            </td>
            <td>
              <div>
                <input type="checkbox" value="2" checked={false} />
              </div>
            </td>
            <td>
              <div>
                <input type="checkbox" value="3" checked={false} />
              </div>
            </td>
          </tr>
        ))}
      </>
    );



        <h4 className="text-center mb-5">Tipo de Documento</h4>
        <form className="CheckBox-main mb-4">
          <input className="Checkbox-1"
            type="checkbox"


          /> Cc



          <input className="Checkbox-2"
            type="checkbox"

          /> Ti



          <input className="Checkbox-2"
            type="checkbox"

          /> Ce



          <input className="Checkbox-3"
            type="checkbox"

          /> Nip



        </form>

        <div className="form-group input-group">
          <div className="input-group-text bg-light mb-2">
            <i className="material-icons">chrome_reader_mode</i>
          </div>

          <input
            type="text"
            placeholder="Ingresa el No. de documento"
            className="form-control"
            value={values.document}
            name="document"
            onChange={handleInputChange}
          />
        </div>
        <h7 className="text-left">Fecha de nacimiento</h7>
        <div className="form-group input-group mb-4">

          <input
            type="date"
            name="date_of_birth"
            placeholder="date_of_birth"
            className="form-control"
            onChange={handleInputChange}


          />
        </div>
        <h7 className="text-left">Fecha de ingreso</h7>
        <div className="form-group input-group">
          <input
            type="date"
            name="date_of_admission"
            placeholder="date_of_admission"
            className="form-control"
            onChange={handleInputChange}
          />
        </div>

        <h7 className="text-left">Fecha de egreso</h7>
        <div className="form-group input-group mb-5">
          <input
            type="date"
            name="egress_date"
            placeholder="egress_date"
            className="form-control"
            onChange={handleInputChange}
          />
        </div>

        <div className="form-group input-group">
          <div className="input-group-text bg-light mb-2">
            <i className="material-icons">school</i>
          </div>

          <input
            type="text"
            name="trimester"
            placeholder="Ingresa el No.trimestre"
            className="form-control"
            value={values.trimester}
            onChange={handleInputChange}
          />
        </div>


        <form className="mb-3">
          <input className="Checkbox-terms mb-3"
            onChange=""
            type="checkbox"
            name=""
            value=""
          /> Acepto términos y condiciones.
        </form>

        <button className="btn btn-primary btn-block">
          {props.currentId === "" ? "Enviar" : "Update"}
        </button>
      </form>
    </div>
  );
};

  }
}


export default LinksForm;
