import React, { useState, useEffect } from "react";
import { db } from "../firebase";
import {
  Table,
  Button,
  Container,
  Modal,
  ModalHeader,
  ModalBody,
  FormGroup,
  ModalFooter,
} from "reactstrap";
import "./LinksForm.css";


const data = [
  { id: 1, personaje: "Naruto", anime: "Naruto" },
  { id: 2, personaje: "Goku", anime: "Dragon Ball" },
  { id: 3, personaje: "Kenshin Himura", anime: "Rurouni Kenshin" },
  { id: 4, personaje: "Monkey D. Luffy", anime: "One Piece" },
  { id: 5, personaje: "Edward Elric", anime: "Fullmetal Alchemist: Brotherhood"},
  { id: 6, personaje: "Seto Kaiba", anime: "Yu-Gi-Oh!" },
];

class App extends React.Component {
  state = {
    data: data,
    modalActualizar: false,
    modalInsertar: false,
    form: {
      id: "",
      personaje: "",
      anime: "",
    },
  };

  mostrarModalActualizar = (dato) => {
    this.setState({
      form: dato,
      modalActualizar: true,
    });
  };

  cerrarModalActualizar = () => {
    this.setState({ modalActualizar: false });
  };

  mostrarModalInsertar = () => {
    this.setState({
      modalInsertar: true,
    });
  };

  cerrarModalInsertar = () => {
    this.setState({ modalInsertar: false });
  };

  editar = (dato) => {
    var contador = 0;
    var arreglo = this.state.data;
    arreglo.map((registro) => {
      if (dato.id == registro.id) {
        arreglo[contador].personaje = dato.personaje;
        arreglo[contador].anime = dato.anime;
      }
      contador++;
    });
    this.setState({ data: arreglo, modalActualizar: false });
  };

  eliminar = (dato) => {
    var opcion = window.confirm("Estás Seguro que deseas Eliminar el elemento "+dato.id);
    if (opcion == true) {
      var contador = 0;
      var arreglo = this.state.data;
      arreglo.map((registro) => {
        if (dato.id == registro.id) {
          arreglo.splice(contador, 1);
        }
        contador++;
      });
      this.setState({ data: arreglo, modalActualizar: false });
    }
  };

  insertar= ()=>{
    var valorNuevo= {...this.state.form};
    valorNuevo.id=this.state.data.length+1;
    var lista= this.state.data;
    lista.push(valorNuevo);
    this.setState({ modalInsertar: false, data: lista });
  }

  handleChange = (e) => {
    this.setState({
      form: {
        ...this.state.form,
        [e.target.name]: e.target.value,
      },
    });
  };

  render() {




const LinksForm = (props) => {
  const initialStateValues = {
    NombreCompleto: "",
    Formación: "",
    TipoDeDocumento: "",
    No_Documento: "",
    FechaDeNacimiento: "",
    FechaDeIngreso: "",
    FechaDeEgreso: "",
    No_Trimestre: "",

  };

  const [values, setValues] = useState(initialStateValues);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setValues({ ...values, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    props.addOrEditLink(values);
    setValues({ ...initialStateValues });
  };

  const getLinkById = async (id) => {
    const doc = await db.collection("Formulario Realizado").doc(id).get();
    setValues({ ...doc.data() });
  };

  useEffect(() => {
    if (props.currentId === "") {
      setValues({ ...initialStateValues });
    } else {
      getLinkById(props.currentId);
    }
  }, [props.currentId]);

  return (
    <div className="container-main">
      <form onSubmit={handleSubmit} className="card card-body border-primary">
        <h1 className="mb-4 text-center">Formulario</h1>
        <div className="form-group input-group">
          <div className="input-group-text bg-light">
            <i className="material-icons">person</i>
          </div>
          <input
            type="text"
            value={values.NombreCompleto}
            name="NombreCompleto"
            placeholder="Ingresa tu nombre completo"
            className="form-control"
            onChange={handleInputChange}
          />
        </div>

        <div className="form-group input-group">
          <div className="input-group-text bg-light mb-4">
            <i className="material-icons">people</i>
          </div>
          <input
            type="text"
            className="form-control"
            placeholder="Formación"
            value={values.Formación}
            name="Formación"
            onChange={handleInputChange}
          />
        </div>

        <h4 className="text-center mb-5">Tipo de documento</h4>
        <select value={values.TipoDeDocumento} onChange={handleInputChange} name="TipoDeDocumento" className="Select">
          <option>Seleccione su documento de identidad</option>
          <option>Cedula ciudadania</option>
          <option>Tarjeta identidad</option>
          <option>Cedula extranjeria</option>
          <option>Numero de identificación personal</option>
          <option>Numero de identificacion tributaria</option>
          <option>Pasaporte</option>
        </select>
        <br />
        <br />

        <div className="form-group input-group">
          <div className="input-group-text bg-light mb-2">
            <i className="material-icons">chrome_reader_mode</i>
          </div>

          <input
            type="number"
            placeholder="Ingresa el No. de documento"
            className="form-control"
            value={values.No_Documento}
            name="No_Documento"
            onChange={handleInputChange}
          />
        </div>
        <h7 className="text-left">Fecha de nacimiento</h7>
        <div className="form-group input-group mb-4">
          <input
            type="date"
            name="FechaDeNacimiento"
            placeholder="date_of_birth"
            value={values.FechaDeNacimiento}
            className="form-control"
            onChange={handleInputChange}
          />
        </div>
        <h7 className="text-left">Fecha de ingreso</h7>
        <div className="form-group input-group">
          <input
            type="date"
            name="FechaDeIngreso"
            placeholder="date_of_admission"
            value={values.FechaDeIngreso}
            className="form-control"
            onChange={handleInputChange}
          />
        </div>

        <h7 className="text-left">Fecha de egreso</h7>
        <div className="form-group input-group mb-5">
          <input
            type="date"
            name="FechaDeEgreso"
            placeholder="egress_date"
            value={values.FechaDeEgreso}
            className="form-control"
            onChange={handleInputChange}
          />
        </div>

        <div className="form-group input-group">
          <div className="input-group-text bg-light mb-2">
            <i className="material-icons">school</i>
          </div>

          <input
            type="number"
            name="No_Trimestre"
            placeholder="Ingresa el No.trimestre"
            className="form-control"
            value={values.No_Trimestre}
            onChange={handleInputChange}
          />
        </div>

        <form className="mb-3">
          <input
            className="Checkbox-terms mb-3"
            type="checkbox"
            name=""
            value=""
          />{""}
          Acepto términos y condiciones.
        </form> 


        <button className="btn btn-primary btn-block">
          {props.currentId === "" ? "Enviar" : "Update"}
        </button>
      </form>
      <>
        <Container>
        <br />
          <Button color="success" onClick={()=>this.mostrarModalInsertar()}>Crear</Button>
          <br />
          <br />
          <Table>
            <thead>
              <tr>
                <th>ID</th>
                <th>Personaje</th>
                <th>Anime</th>
                <th>Acción</th>
              </tr>
            </thead>

            <tbody>
              {this.state.data.map((dato) => (
                <tr key={dato.id}>
                  <td>{dato.id}</td>
                  <td>{dato.personaje}</td>
                  <td>{dato.anime}</td>
                  <td>
                    <Button
                      color="primary"
                      onClick={() => this.mostrarModalActualizar(dato)}
                    >
                      Editar
                    </Button>{" "}
                    <Button color="danger" onClick={()=> this.eliminar(dato)}>Eliminar</Button>
                  </td>
                </tr>
              ))}
            </tbody>
          </Table>
        </Container>

        <Modal isOpen={this.state.modalActualizar}>
          <ModalHeader>
           <div><h3>Editar Registro</h3></div>
          </ModalHeader>

          <ModalBody>
            <FormGroup>
              <label>
               Id:
              </label>

              <input
                className="form-control"
                readOnly
                type="text"
                value={this.state.form.id}
              />
            </FormGroup>

            <FormGroup>
              <label>
                Personaje: 
              </label>
              <input
                className="form-control"
                name="personaje"
                type="text"
                onChange={this.handleChange}
                value={this.state.form.personaje}
              />
            </FormGroup>

            <FormGroup>
              <label>
                Anime: 
              </label>
              <input
                className="form-control"
                name="anime"
                type="text"
                onChange={this.handleChange}
                value={this.state.form.anime}
              />
            </FormGroup>
          </ModalBody>

          <ModalFooter>
            <Button
              color="primary"
              onClick={() => this.editar(this.state.form)}
            >
              Editar
            </Button>
            <Button
              color="danger"
              onClick={() => this.cerrarModalActualizar()}
            >
              Cancelar
            </Button>
          </ModalFooter>
        </Modal>



        <Modal isOpen={this.state.modalInsertar}>
          <ModalHeader>
           <div><h3>Insertar Personaje</h3></div>
          </ModalHeader>

          <ModalBody>
            <FormGroup>
              <label>
                Id: 
              </label>

              <input
                className="form-control"
                readOnly
                type="text"
                value={this.state.data.length+1}
              />
            </FormGroup>

            <FormGroup>
              <label>
                Personaje: 
              </label>
              <input
                className="form-control"
                name="personaje"
                type="text"
                onChange={this.handleChange}
              />
            </FormGroup>

            <FormGroup>
              <label>
                Anime: 
              </label>
              <input
                className="form-control"
                name="anime"
                type="text"
                onChange={this.handleChange}
              />
            </FormGroup>
          </ModalBody>

          <ModalFooter>
            <Button
              color="primary"
              onClick={() => this.insertar()}
            >
              Insertar
            </Button>
            <Button
              className="btn btn-danger"
              onClick={() => this.cerrarModalInsertar()}
            >
              Cancelar
            </Button>
          </ModalFooter>
        </Modal>
      </>
    );

    </div>

  );
};
  }

}

export default LinksForm;
