import React, { useState, useEffect, Checkbox, Component } from "react";
import { db } from "../firebase";
import CheckboxGroup from 'react-checkbox-group'
import "./LinksForm.css"

const Demo = () => {
  // Initialize the checked values
  const [fruits, setFruits] = useState < string > (['apple', 'watermelon'])

  useEffect(() => {
    const timer = setTimeout(() => {
      setFruits(['apple', 'orange'])
    }, 5000)

    return () => clearTimeout(timer)
  }, [])

  return (
    <CheckboxGroup name="fruits" value={fruits} onChange={setFruits}>
      {(Checkbox) => (
        <>
          <label>
            <Checkbox value="apple" /> Apple
          </label>
          <label>
            <Checkbox value="orange" /> Orange
          </label>
          <label>
            <Checkbox value="watermelon" /> Watermelon
          </label>
        </>
      )}
    </CheckboxGroup>
  )
}



const LinksForm = (props) => {
  const initialStateValues = {
    url: "",
    name: "",


  };

  const [values, setValues] = useState(initialStateValues);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setValues({ ...values, [name]: value });
  };


  const handleSubmit = (e) => {
    e.preventDefault();


    props.addOrEditLink(values);
    setValues({ ...initialStateValues });
  };

  const getLinkById = async (id) => {
    const doc = await db.collection("links").doc(id).get();
    setValues({ ...doc.data() });
  };

  useEffect(() => {
    if (props.currentId === "") {
      setValues({ ...initialStateValues });
    } else {
      getLinkById(props.currentId);
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [props.currentId]);




  return (


    <form onSubmit={handleSubmit} className="card card-body border-primary">
      <h1 className="mb-4 text-center">Alumnos</h1>
      <div className="form-group input-group">
        <div className="input-group-text bg-light">
          <i className="material-icons">person</i>
        </div>
        <input
          type="text"
          value={values.name}
          name="name"
          placeholder="Ingresa tu nombre completo"
          className="form-control"
          onChange={handleInputChange}
        />
      </div>

      <div className="form-group input-group">
        <div className="input-group-text bg-light">
          <i className="material-icons">people</i>
        </div>
        <input 
          type="text"
          className="form-control"
          placeholder="FormaciÃ³n"
          value={values.url}
          name="url"
          onChange={handleInputChange}
          />
      </div>


<h4 className="text-center mb-4">Tipo de Documento</h4>
      <form className="Checkbox-main">
        <input className="Checkbox-1"
          onChange="{handleFruitChange}"
          type="checkbox"
          name="fruit"
          value="apple"
        />Cc
        <input className="Checkbox-2"
          onChange="{handleFruitChange}"
          type="checkbox"
          name="fruit"
          value="orange"
        />Ti
        <input className="Checkbox-3"
          onChange="{handleFruitChange}"
          type="checkbox"
          name="fruit"
          value="watermelon"
        />Ce

      </form>

      <div className="form-group input-group">
        <div className="input-group-text bg-light mb-3">
          <i className="material-icons">chrome_reader_mode</i>
        </div>

        <input 
          type="text"
          name="name"
          placeholder="Ingresa el numero de documento"
          className="form-control"     
        />
      </div>


      <div className="form-group input-group">
        <div className="input-group-text bg-light mb-3">
          <i className="material-icons">date_range</i>
        </div>

        <input 
          type="text"
          name="name"
          placeholder=""
          className="form-control"     
        />
      </div>





      <button className="btn btn-primary btn-block">
        {props.currentId === "" ? "Enviar" : "Update"}
      </button>
    </form>
  );
};





export default LinksForm;
